import { useEffect, useReducer, useState } from 'react';
import axios from 'axios';
import logger from 'use-reducer-logger';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Product from '../components/Product';
import { Helmet } from 'react-helmet-async';
import MessageBox from '../components/MessageBox';
import LoadingBox from '../components/LoadingBox';
import ChatBot from '../components/ChatBot';
// import data from '../data';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, products: action.payload, loading: false };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function HomeScreen() {
  const [{ loading, error, products }, dispatch] = useReducer(logger(reducer), {
    products: [],
    loading: true,
    error: '',
  });

  const [isPopupOpen, setIsPopupOpen] = useState(false); // New state for popup visibility

  // const [products, setProducts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get('https://greenlife-deploy-5.onrender.com/api/products');
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }

      // setProducts(result.data);
    };
    fetchData();
  }, []);


  const showPopup = () => {
    setIsPopupOpen(true);
  };

  const hidePopup = () => {
    setIsPopupOpen(false);
  };

  return (
    <div>
      <Helmet>
        <title>GreenLife</title>
      </Helmet>
      <h1>Sản phẩm mới</h1>
      <div className="products">
        {loading ? (
           <LoadingBox />
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
          <Row>
            {products.map((product) => (
              <Col key={product.slug} sm={6} md={4} lg={3} className="mb-3">
                <Product product={product}></Product>
              </Col>
            ))}
          </Row>
        )}
      </div>
      <button id="subscribeBtn" className="product-item-info-button" onClick={showPopup}>
        Đăng ký
      </button>
      {isPopupOpen && (
        <div id="subscribeOverlay" className="subscribe-overlay" style={{ display: 'block' }}>
          <div id="subscribePopup" className="subscribe-popup" style={{ display: 'block' }}>
            {/* ... your popup form JSX here */}
          </div>
        </div>
      )}
    </div>
  );
}
export default HomeScreen;
